import React, {Component} from 'react'
import * as d3 from "d3"

const width = 960;
const height = 500;
const margin = {top: 50, right: 50, bottom: 100, left:50};

class LineChart extends Component {

  componentDidMount() {
    this.drawChart(this.props.data)
  }

  componentDidUpdate(){
    this.update(this.props.data, this.props.threshold)
  }

  drawChart(data) {
    // ------- scale ---------//
    // let new_data = data.filter(function(d) {return d.date <
    var startDate = data.dates[0],
        endDate = data.dates[data.dates.length-1];

    var maxi = d3.max(data.series, s => d3.max(s.positive))

    var xScale = d3.scaleTime()
      .domain([startDate, endDate])
      .range([margin.left, width - margin.right]);

    var yScale = d3.scaleLinear()
      // d3.max(data, d => d.positive)
      .domain ([0, maxi]).nice()
      .range([height - margin.bottom, margin.top])

    // --------- axis --------//
    const xAxis = d3.axisBottom()
      .ticks(d3.timeDay.every(1))
      .tickFormat(d3.timeFormat('%m/%d'))
      .scale(xScale);

    const yAxis = d3.axisLeft()
      .ticks(10)
      .scale(yScale);

    // ------- draw --------//
    this.svg = d3.select(this.refs.chart)
      .append("svg")
      .attr('width', width )
      .attr('height', height )

    this.svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis);

    this.svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);

    //this.chart = this.svg.append("g")
    }


    update(data, threshold){
      // let new_data = data.filter(function(d) {return d.date <

      var new_data = data.series.map(d => ({
          name: d.name,
          positive: d.positive.filter(date => date < threshold)
        }))

      console.log(new_data)

      var startDate = data.dates[0],
          endDate = data.dates[data.dates.length-1];

      var maxi = d3.max(data.series, s => d3.max(s.positive))

      var xScale = d3.scaleTime()
        .domain([startDate, endDate])
        .range([margin.left, width - margin.right]);

      var yScale = d3.scaleLinear()
        .domain ([0, maxi]).nice()
        .range([height - margin.bottom, margin.top])

      var line = d3.line()
        .x(function(d,i) { return xScale(data.dates[i]); })
        .y(d => yScale(d.positive))

      const chart = this.svg.append('g')

      chart.selectAll("path")
        .data(new_data.series)
          .join("path")
            .attr("stroke-width", 2)
            .attr("stroke", "steelblue")
            .attr("fill", "none")
            .attr("d", line)


      chart.selectAll('circle')
        .data(new_data.series)
        .join("circle")
         .attr("r", 4.5)
         .attr("stroke", "steelblue")
         .attr("fill", "steelblue")
         .attr("cx", function(d,i) { return xScale(data.dates[i]); })
         .attr("cy", function(d) { return yScale(d.positive); })

    }

  render() {return <div ref="chart"></div>}
}


export default LineChart;
