import React, {Component} from 'react'
import * as d3 from "d3"

const width = 960;
const height = 500;
const margin = {top: 50, right: 50, bottom: 100, left:50};

class LineChart extends Component {
  constructor(){
    super();
    this.state = {
      data: {}
    }
  }

  componentDidMount() {
    this.setState({data:this.props.data})
    this.drawChart(this.state.data)
  }

  componentDidUpdate(){
    this.update(this.state.data, this.props.threshold)
  }

  drawChart(data) {
    // ------- scale ---------//
    var xScale = d3.scaleTime()
      .domain(d3.extent(data, d => d.date))
      .range([margin.left, width - margin.right]);

    var yScale = d3.scaleLinear()
      .domain ([0, d3.max(data, d => d.positive)]).nice()
      .range([height - margin.bottom, margin.top])

    // --------- axis --------//
    const xAxis = d3.axisBottom()
      .ticks(d3.timeDay.every(1))
      .tickFormat(d3.timeFormat('%m/%d'))
      .scale(xScale);

    const yAxis = d3.axisLeft()
      .ticks(10)
      .scale(yScale);

    // ------- draw --------//
    this.svg = d3.select(this.refs.chart)
      .append("svg")
      .attr('width', width )
      .attr('height', height )

    this.svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis);

    this.svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);
    }


    update(data, threshold){

      console.log(threshold)
      var new_data = data.filter(function(d) {return d.date < threshold;});

      var xScale = d3.scaleTime()
        .domain(d3.extent(data, d => d.date))
        .range([margin.left, width - margin.right]);

      var yScale = d3.scaleLinear()
        .domain ([0, d3.max(data, d => d.positive)]).nice()
        .range([height - margin.bottom, margin.top])

      var line = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.positive))

      this.chart = this.svg.append("g")

      this.chart.append("path")
          .datum(new_data)
            .attr("stroke-width", 2)
            .attr("stroke", "steelblue")
            .attr("fill", "none")
            .attr("d", line)


      this.chart.selectAll('circle')
        .data(new_data)
        .join(
          enter => enter.append("circle"),
          update => update,
          exit => exit.remove()
        )
         .attr("r", 4.5)
         .attr("stroke", "steelblue")
         .attr("fill", "steelblue")
         .attr("cx", function(d) { return xScale(d.date); })
         .attr("cy", function(d) { return yScale(d.positive); })

    }

  render() {return <div ref="chart"></div>}
}


export default LineChart;
