import React, {Component} from 'react'
import * as d3 from "d3"
import {sliderBottom} from 'd3-simple-slider'


class LineChart extends Component {
  componentDidMount() {
    this.drawChart(this.props.data)
  }

  drawChart(data) {
    const width = 960;
    const height = 500;
    const margin = {top: 50, right: 50, bottom: 100, left:50};

    const xScale = d3.scaleTime()
      .domain(d3.extent(data, d => d.date))
      .range([margin.left, width - margin.right]);

    const yScale = d3.scaleLinear()
      .domain ([0, d3.max(data, d => d.positive)]).nice()
      .range([height - margin.bottom, margin.top])

    const xAxis = d3.axisBottom()
      .ticks(d3.timeDay.every(1))
      .tickFormat(d3.timeFormat('%m/%d'))
      .scale(xScale);

    const yAxis = d3.axisLeft()
      .ticks(10)
      .scale(yScale);

    const line = d3.line()
      .x(d => xScale(d.date))
      .y(d => yScale(d.positive))

    const svg = d3.select(this.refs.chart)
      .append("svg")
      .attr('width', width )
      .attr('height', height )

    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis);

    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);

    const chart = svg.append("g")

    chart.append("path")
         .datum(data)
         .attr("stroke-width", 2)
         .attr("stroke", "steelblue")
         .attr("fill", "none")
         .attr("d", line)

    chart.selectAll('circle')
      .data(data)
      .enter()
        .append("circle")
         .attr("r", 4.5)
         .attr("stroke", "steelblue")
         .attr("fill", "steelblue")
         .attr("cx", function(d) { return xScale(d.date); })
         .attr("cy", function(d) { return yScale(d.positive); })

    const sliderg = svg.append("g")
            .attr("transform", `translate(${margin.left}, ${height-50})`)

    const slide = sliderBottom()
        .min(d3.min(this.props.data, d => d.date))
        .max(d3.max(this.props.data, d => d.date))
        .step(1000 * 60 * 60 * 24)
        .width(width-margin.left-margin.right)
        .ticks(this.props.data.length)
        .tickFormat(d3.timeFormat('%m/%d'))
        .default(d3.min(this.props.data, d => d.date))
        .on('onchange', val => update(val))

    function update(val){

      console.log(slide.value())
      var new_data = data.filter(function(d) {return d.date < val;})


      var path = chart.selectAll('path').data(new_data)

      var circles = chart.selectAll('circle').data(new_data)

      path.enter()
          .attr("stroke-width", 2)
          .attr("stroke", "steelblue")
          .attr("fill", "none")
          .attr("d", line)

      path.exit.remove()

      circles.enter()
            .append("circle")
             .attr("r", 4.5)
             .attr("stroke", "steelblue")
             .attr("fill", "steelblue")
             .attr("cx", function(d) { return xScale(d.date); })
             .attr("cy", function(d) { return yScale(d.positive); })

      circles.exit.remove()
    }


    sliderg.call(slide)


  }

  render() {return <div ref="chart"></div>}
}


export default LineChart;
