import React, { Component } from 'react'
import LineChart from './LineChart'
import Slider from './slider'
import StateSelector from './StateSelector'
import datajson from './us.json'

console.log(datajson)

const data = {

  series : [
    {
      name: "US",
      positive: datajson.map(d => d.positive)
    }
  ],
  dates: datajson.map (d => new Date(d.date))
}

console.log(data)

  class App extends Component {

    constructor(){
      super();
      this.state = {
        data : data,
        threshold : data.dates[0],
        country: "US",
        states: []

      }
    }

    updateThreshold = (data) => {
      this.setState({threshold : data})
    }

    updateStateSelected = (chosen) => {
      console.log(this.state.states.concat([chosen]))
      this.setState({states: this.state.states.concat([chosen]) })
    }


    onClearClick = (e) => {
      e.preventDefault()
      console.log("here")
      this.setState({states: []})
      console.log(this.state.states)
    }



    render () {
      return (
        <div className = "App">
        <div>
            {this.state.states}
            <StateSelector updateToParent = {this.updateStateSelected}/>
            <button type="button" onclick = {this.onClearClick}>Clear</button>
        </div>
        </div>
      )
    }

  }


export default App;
