import React, { Component } from 'react'
import LineChart from './LineChart'
import Slider from './slider'
import datajson from './us.json'

console.log(datajson)

for (var i=0; i < datajson.length; i++){
  datajson[i].date = new Date(datajson[i].date)
}

  class App extends Component {

    constructor(){
      super();
      this.state = {
        data : datajson,
        threshold: new Date()
      }
    }

    updateDate= (date) => {
      this.setState({threshold : date})
    }



    render () {
      return (
        <div className = "App">
        <div>
            <LineChart data= {this.state.data} threshold={this.state.threshold}/>
            <Slider data= {this.state.data} updateToParent = {this.updateDate}/>
        </div>
        </div>
      )
    }

  }


export default App;
