import React, {Component} from 'react'
import * as d3 from "d3"
import {sliderBottom} from 'd3-simple-slider'


class LineChart extends Component {
  componentDidMount() {
    this.drawChart(this.props.data)
  }

  drawChart(data) {
    const width = 960;
    const height = 500;
    const margin = {top: 50, right: 50, bottom: 100, left:50};

    const xScale = d3.scaleTime()
      .domain(d3.extent(data, d => d.date))
      .range([margin.left, width - margin.right]);

    const yScale = d3.scaleLinear()
      .domain ([0, d3.max(data, d => d.positive)]).nice()
      .range([height - margin.bottom, margin.top])

    const xAxis = d3.axisBottom()
      .ticks(d3.timeDay.every(1))
      .tickFormat(d3.timeFormat('%m/%d'))
      .scale(xScale);

    const yAxis = d3.axisLeft()
      .ticks(10)
      .scale(yScale);

    const line = d3.line()
      .x(d => xScale(d.date))
      .y(d => yScale(d.positive))

    const svg = d3.select(this.refs.chart)
      .append("svg")
      .attr('width', width )
      .attr('height', height )

    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis);

    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);

    const chart = svg.append("g")

    chart.append("path")
         .datum(data)
         .attr("stroke-width", 2)
         .attr("stroke", "steelblue")
         .attr("fill", "none")
         .attr("d", line)

    chart.selectAll('circle')
      .data(data)
      .enter()
        .append("circle")
         .attr("r", 4.5)
         .attr("stroke", "steelblue")
         .attr("fill", "steelblue")
         .attr("cx", function(d) { return xScale(d.date); })
         .attr("cy", function(d) { return yScale(d.positive); })

    const slider = svg.append("g")
            .attr("transform", `translate(${margin.left}, ${height-50})`)

    slider.append("line")
      .attr("x1",xAxis.range([0]))
      .attr("x2", xAxis.range([1]))
    .select(function() { return this.parentNode.appendChild(this.cloneNode(true)); })
      .attr("class", "track-inset")
    .select(function() { return this.parentNode.appendChild(this.cloneNode(true)); })
      .attr("class", "track-overlay")
      .call(d3.drag()
          .on("start.interrupt", function() { slider.interrupt(); })
          .on("start drag", function() {console.log(d3.event.x)}));


    const formatDate = d3.timeFormat("%m/%d");

    slider.insert("g", ".track-overlay")
    .attr("class", "ticks")
    .attr("transform", "translate(0," + 18 + ")")
  .selectAll("text")
    .data(xScale)
    .enter()
    .append("text")
    .attr("x", xScale)
    .attr("y", 10)
    .attr("text-anchor", "middle")
    .text(function(d) { return formatDate(d); });

    // var label = slider.append("text")
    //     .attr("class", "label")
    //     .attr("text-anchor", "middle")
    //     .text(formatDate(startDate))
    //     .attr("transform", "translate(0," + (-25) + ")")

    var handle = slider.insert("circle", ".track-overlay")
        .attr("class", "handle")
        .attr("r", 9);



    function update(val){

      var new_data = data.filter(function(d) {return d.date < val;})


      var path = chart.selectAll('path').data(new_data)

      var circles = chart.selectAll('circle').data(new_data)

      path.enter()
          .attr("stroke-width", 2)
          .attr("stroke", "steelblue")
          .attr("fill", "none")
          .attr("d", line)

      path.exit.remove()

      circles.enter()
            .append("circle")
             .attr("r", 4.5)
             .attr("stroke", "steelblue")
             .attr("fill", "steelblue")
             .attr("cx", function(d) { return xScale(d.date); })
             .attr("cy", function(d) { return yScale(d.positive); })

      circles.exit.remove()
    }




  }

  render() {return <div ref="chart"></div>}
}


export default LineChart;
