import React, { Component } from 'react'
import LineChart from './LineChart'
import Slider from './slider'
import datajson from './us.json'

console.log(datajson)

const columns = datajson.columns

data = {

  series : [
    {
      name: "US",
      positive: datajson.map(d.positive)
    }
  ],
  dates: datajson.map (d => new Date(d.date))
}

console.log(data)

  class App extends Component {

    constructor(){
      super();
      this.state = {
        data : data,
        json : datajson,
        data_rendering :{}
      }
    }

    updateData = (data) => {
      this.setState({data_rendering : data})
    }



    render () {
      return (
        <div className = "App">
        <div>
            {this.state.data_rendering.length}
            <LineChart data= {this.state.data} data_to_show={this.state.data_rendering}/>
            <Slider data= {this.state.data} updateToParent = {this.updateData}/>
        </div>
        </div>
      )
    }

  }


export default App;
